package replication;

public class replica {

// TODO Auto-generated method stub
		public static void main(String[] args) {
		// TODO Auto-generated method stub
		repl("Samuel", 3);
		repl("Ashagre",0);
		repl("Belvue college", -1);
		
		
	}

	public static void repl(String name, int times) {
		for (int i=1; i<=times; i++) {
			System.out.print(name);
			
		}
			

	
	
    }
}
